mapper.select:
select ci.corp_id corpId, ci.corp_name corpName,ci.reg_cap regCap, ci.corp_type corpType, ci.is_country isCountry, ci.sys_type sysType, ci.note note, ci.legal_person legalPerson,ci.ccy ccy, ci.control_person controlPerson, ci.fixed_phone fixedPhone, ci.reg_date regDate, ci.logo_url logoUrl, ci.staff_num staffNum, ci.real_pay_cap realPayCap, ci.industry industry, ci.reg_address regAddress,ci.core_corp_id coreCorpId,ci.loan_card_no loanCardNo, ci.office_address officeAddress, ci.history_evc historyEvc, ci.spec_natural specNatural, ci.busi_scope busiScope,ci.core_corp_name coreCorpName, ci.app_num appNum,ci.open_account_licence openAccountLicence,ci.customer_app_serial_num customerAppSerialNum, ci.orgn_credit_num orgnCreditNum, ci.orgn_num orgnNum,ci.business_license businessLicense,ci.applicant_name applicantName,ci.applicant_phone applicantPhone, ci.applicant_fix_phone applicantFixPhone,ci.apply_amt applyAmt,ci.is_push isPush,ci.prd_status prdStatus, IFNULL(a.num,0) supplierNum, ci.job job,ci.email email from corp_info ci LEFT JOIN (SELECT COUNT(1) num, core_corp_id corp_id FROM corp_info WHERE core_corp_id IS NOT NULL GROUP BY core_corp_id) a ON ci.corp_id = a.corp_id
ci.status=1
and ci.corp_id = #{corpId}
and ci.corp_id in ${userCorpList}
and ci.corp_name = #{corpName}
and ci.corp_name LIKE CONCAT(CONCAT('%', #{corpName}),'%')
and ci.legal_person LIKE CONCAT(CONCAT('%', #{legalPerson}),'%')
and ci.control_person LIKE CONCAT(CONCAT('%', #{controlPerson}),'%')
and ci.sys_type=#{sysType}
and ci.prd_status = #{prdStatus}
and ci.create_user_id = #{createUserId}

    select count(1)
    from corp_info d
    where d.status=1 and  d.corp_name = #{corpName}

		 	and d.corp_id!= #{corpId}


		 	and d.sys_type= #{sysType}

select count(1)
from corp_dept d
where d.status=1 and d.corp_id = #{corpId}
