mapper.select:
select su.user_id userId,sr.role_type roleType, su.username username,su.realname realname, sr.role_id roleId, sr.role_name roleName, sr.role_type roleType,su.mobilephone mobilephone,su.photo_url photoUrl,su.note note,su.email email,su.fixed_phone fixedPhone, cd.dept_id deptId,cd.dept_name deptName,su.corp_id corpId,ci.corp_name corpName
from sys_user su join sys_user_role sur on su.user_id=sur.user_id join sys_role sr on sur.role_id=sr.role_id left join corp_dept cd on su.dept_id=cd.dept_id left join corp_info ci on ci.corp_id=cd.corp_id
su.status=1 and su.user_id!='u00001'
and sr.role_type = #{roleType}
and su.username LIKE CONCAT(CONCAT('%', #{username}),'%')
and su.mobilephone LIKE CONCAT(CONCAT('%', #{mobilephone}),'%')
and sr.role_name LIKE CONCAT(CONCAT('%', #{roleName}),'%')
and sr.role_id = #{roleId}
and ci.corp_id = #{corpId}

select su.user_id userId, su.username username,su.realname realname, sur.role_id roleId, sr.role_name roleName, sr.role_type roleType,su.mobilephone mobilephone,su.photo_url photoUrl,su.note note,su.email email,su.fixed_phone fixedPhone, cd.dept_id deptId,cd.dept_name deptName,su.corp_id corpId,ci.corp_name corpName
from sys_user su join sys_user_role sur on su.user_id=sur.user_id join sys_role sr on sur.role_id=sr.role_id join sys_role_menu rm on sr.role_id=rm.role_id join sys_menu m on rm.menu_id=m.menu_id left join corp_dept cd on su.dept_id=cd.dept_id left join corp_info ci on ci.corp_id=cd.corp_id
su.status=1 and m.status=1
and sr.role_type = #{roleType}
and su.username LIKE CONCAT(CONCAT('%', #{username}),'%')
and su.mobilephone LIKE CONCAT(CONCAT('%', #{mobilephone}),'%')
and sr.role_id = #{roleId}
and ci.corp_id = #{corpId}
and m.menu_id = #{menuId}

mapper.update:
update sys_user
set  password = #{newPassword}
where user_id = #{userId} and password = #{oldPassword}

update sys_user
set  password = '670b14728ad9902aecba32e22fa4f6bd'
