mapper.select:
SELECT ci.corp_id "corpId", ci.corp_name "corpName" FROM corp_info ci
ci.status=1
AND ci.sys_type = #{sysType}

select ci.corp_id "corpId", ci.corp_name "corpName" from corp_info ci
ci.status=1
and ci.sys_type = #{sysType}

mapper.update:
UPDATE corp_info
SET customer_app_serial_num = CASE corp_id

	WHEN #{item.corpId} THEN #{item.customerAppSerialNum}

END WHERE corp_id IN

	#{item.corpId}

mapper.select:
